/**
 * This handler class handles the page events of the Form called by the mUNews_admin_edit() function.
 * It aims on the message object type.
 */
class MUNews_Form_Handler_Admin_Message_Edit extends MUNews_Form_Handler_Admin_Message_Base_Edit
{
    /**
     * Post-initialise hook.
     *
     * @return void
     */
    public function postInitialize()
    {
        $muimageAlbum = $this->getVar('muimageAlbum');
        $this->view->assign('muimageAlbum', $muimageAlbum);
        
        // we check for module MUImage and legacy mode on
        $muimage = ModUtil::available('MUImage');
        $legacy = System::isLegacyMode();
        
        if ($muimage == true && $legacy == true) {
            $useMUImage = true;
        } else {
            $useMUImage = false;
        }
        
        $this->view->assign('useMUImage', $useMUImage);
        
        parent::postInitialize();
    }
}
