declare(strict_types=1);

namespace MU\NewsModule\Entity\Factory\Base;

use DateTime;
use Symfony\Component\HttpFoundation\RequestStack;
use MU\NewsModule\Entity\MessageEntity;
use MU\NewsModule\Entity\ImageEntity;
use MU\NewsModule\Helper\PermissionHelper;

/**
 * Entity initialiser class used to dynamically apply default values to newly created entities.
 */
abstract class AbstractEntityInitialiser
{
    /**
     * @var RequestStack
     */
    protected $requestStack;

    /**
     * @var PermissionHelper
     */
    protected $permissionHelper;

    public function __construct(
        RequestStack $requestStack,
        PermissionHelper $permissionHelper
    ) {
        $this->requestStack = $requestStack;
        $this->permissionHelper = $permissionHelper;
    }

    /**
     * Initialises a given message instance.
     */
    public function initMessage(MessageEntity $entity): MessageEntity
    {
        $entity->setMessageLanguage($this->requestStack->getCurrentRequest()->getLocale());

        return $entity;
    }

    /**
     * Initialises a given image instance.
     */
    public function initImage(ImageEntity $entity): ImageEntity
    {
        return $entity;
    }
}
